package bean;

// TODO: Auto-generated Javadoc
/**
 * The Class Grades.
 *
 * @author naveen
 */
public class Grades {

/** The letter grade. */
private String letterGrade;

/** The number grade. */
private int numberGrade;

/**
 * Gets the letter grade.
 *
 * @return the letterGrade
 */
public String getLetterGrade() {
	return letterGrade;
}

/**
 * Sets the letter grade.
 *
 * @param letterGrade the letterGrade to set
 */
public void setLetterGrade(String letterGrade) {
	this.letterGrade = letterGrade;
}

/**
 * Gets the number grade.
 *
 * @return the numberGrade
 */
public int getNumberGrade() {
	return numberGrade;
}

/**
 * Sets the number grade.
 *
 * @param numberGrade the numberGrade to set
 */
public void setNumberGrade(int numberGrade) {
	this.numberGrade = numberGrade;
}
}
